$('#login-button').click(function (event) {
    event.preventDefault();
    $('form').fadeOut(500);
    $('.wrapper').addClass('form-success');
    requestFullScreen();
    // 发送消息给父页面，通知登录成功
    if (window.parent) {
        window.parent.postMessage('loginSuccess', '*');
    }
    setTimeout(function(){ location.href = "index1.html"; }, 2000);
});
